                    </a>
                    {/* <ReactPlayer url={videoUrl}></ReactPlayer> */}

                </div>

                <p className="video-title">{video.title}</p>
                <p className="video-desc">{video.desc}</p>
                <p className="video-date" >{video.originalAirDate}</p>
            </div>
        ))}
    </>
  );
};

export default VideoList;
