import React, {useState, useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import VideoList from './components/VideoList';
import axios from 'axios';
// import './App.css';

const App = () => {
  const [videos, url, getVideos] = useState([]);
  // const videoUr/l = 'https://www.youtube.com/watch?v=ScMzIvxBSi4';

  // state = {

  //   url: "https://www.youtube.com/watch?v=ScMzIvxBSi4"

  // };

  const getVideoData = () => {
    axios.get(`https://api.sampleapis.com/futurama/episodes`)
      .then(res => {
        const videos = res.data;

        // this.setState({ videos });
        getVideos(videos);
      })
  }



  useEffect(() => {
    const url = 'https://www.youtube.com/watch?v=ScMzIvxBSi4';
    getVideoData();
  }, []);

  return (
      <div className='container-fluid video-dashboard-app'>
        <div className='row'>
          <VideoList videos={videos} url={url}></VideoList>
        </div>
      </div>
  );
}

export default App;
