import React, {useState, useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import VideoList from './components/VideoList';
import axios from 'axios';
// import './App.css';

const App = () => {
  const [videos, getVideos] = useState([
    {
      "number": "1 - 1",
      "title": "Space Pilot 3000",
      "writers": "D.X. Cohen, M. Groening",
      "originalAirDate": "March 28, 1999",
      "desc": "A pizza delivery boy named Philip J. Fry accidentally stumbles into a cryogenic tube on Dеcember 31, 1999 and awakens on New Year's Eve in the year 2999, where he meets a mutant career counselor named Leela, an alcoholic robot named Bender, and his distant nephew Professor Farnsworth.",
      "id": 1
      },
      {
      "number": "2 - 2",
      "title": "The Series Has Landed",
      "writers": "Ken Keeler",
      "originalAirDate": "April 4, 1999",
      "desc": "After delivering a package to an amusement park on the moon, Fry shows Leela how to appreciate the celestial body while Bender and Amy must try to recover the keys to the Planet Express ship.",
      "id": 2
      },
  ]);

  const getVideoData = () => {
    axios.get(`https://api.sampleapis.com/futurama/episodes`)
      .then(res => {
        const videos = res.data;
        this.setState({ videos });
        console.log(this.state.videos);
      })
  }

  useEffect(() => {
    getVideoData();
  }, []);

  return (
      <div className='container-fluid video-dashboard-app'>
        <div className='row'>
          <VideoList videos={videos}></VideoList>
        </div>
      </div>
  );
}

export default App;
